INSERT INTO text(txt_code, txt_title, txt_author, txt_text)
VALUES (#{txtCode}, #{txtTitle}, #{txtAuthor}, #{txtText})
RETURNING txt_id;

UPDATE text
SET
txt_code = #{txtCode}, txt_title=#{txtTitle}, txt_author=#{txtAuthor}, 
txt_text=#{txtText}
WHERE txt_id = #{txtId};

UPDATE text
SET txt_textrank = #{txtTextRank}
WHERE txt_id=#{txtId};
